commits.content:
/* Content */
.content {
    flex-grow: 1;
    padding: 40px;
}

.content-header h2 {
    color: #333;
}

.content-header p {
    margin-bottom: 20px;
}

.contact-form,
.contact-info {
    margin-top: 20px;
}

.contact-form h3,
.contact-info h3 {
    margin-bottom: 10px;
}

.contact-form form {
    display: flex;
    flex-direction: column;
}

.contact-form label {
    margin-top: 10px;
}

.contact-form input,
.contact-form textarea {
    padding: 10px;
    border: 1px solid #ccc;
    width: 100%;
}

.contact-form button {
    margin-top: 15px;
    padding: 10px;
    background: #ff7b00;
    color: white;
    border: none;
    cursor: pointer;
}

.contact-info p {
    display: flex;
    align-items: center;
    gap: 10px;
}

.contact-info a {
    text-decoration: none;
    color: #ff7b00;
}
